/**
 * Prints whatever in a nice, readable format
 * 
 * @param 	mixed 		$input 		Something to pretty print, usually an array or object
 * 
 * @return 	mixed 					The input between pre tags and in print_r()
 */
function pretty( $input ) {

	echo '<pre>'; print_r( $input ); echo '</pre>';

} // pretty()



function change_search_placeholder_text( $form ) {

	 $form = '<form role="search" method="get" class="search-form" action="' . esc_url( home_url( '/' ) ) . '">
	 		<label><span class="screen-reader-text">' . _x( 'Search for:', 'label' ) . '</span>
	 			<input type="search" class="search-field" placeholder="' . esc_attr_x( 'Search', 'placeholder' ) . '" value="' . get_search_query() . '" name="s" title="' . esc_attr_x( 'Search for:', 'label' ) . '" />
	 		</label>
	 		<input type="submit" class="search-submit" value="'. esc_attr_x( 'Search', 'submit button' ) .'" />
	 	</form>';

    return $form;

} // change_search_placeholder_text()

add_filter( 'get_search_form', 'change_search_placeholder_text' );



/**
 * Alters the WP_Query for the home page
 *
 * @param 	string 		$query 		The WP_Query object
 *
 * @uses 	is_home()
 * @uses 	is_main_query()
 * @uses 	set()
 *
 * @return  object 					A modified query object
 */
function get_home_posts( $query ) {
	
	if ( $query->is_home() && $query->is_main_query() ) {

		$query->set( 'posts_per_page', 5 );
		return;

	}

} // get_home_posts()

add_action( 'pre_get_posts', 'get_home_posts' );



function add_svg_to_captions( $caption, $id, $item, $data, $i ) {

	$caption = get_svg( 'check' ) . $caption;

	return $caption;

} // add_svg_to_captions()

add_filter( 'soliloquy_output_caption', 'add_svg_to_captions', 10, 5 );



function add_svg_to_container( $slider, $data ) {

	$slider = $slider . get_svg( 'moment' );

	return $slider;

} // add_svg_to_container()

add_filter( 'soliloquy_output_before_container', 'add_svg_to_container', 10, 2 );


/**
 * Returns the URL of the featured image
 * 
 * @param 	int 		$postID 		The post ID
 * @param 	string 		$size 			The image size to return
 * 
 * @return 	string | bool 				The URL of the featured image, otherwise FALSE
 */
function get_thumbnail_url( $postID, $size = 'thumbnail' ) {

	if ( empty( $postID ) ) { return FALSE; }

	$thumb_id = get_post_thumbnail_id( $postID );

	if ( empty( $thumb_id ) ) { return FALSE; }

	$thumb_array = wp_get_attachment_image_src( $thumb_id, $size, true );

	if ( empty( $thumb_array ) ) { return FALSE; }
	
	return $thumb_array[0];

} // get_thumbnail_url()




